import React, { useState } from "react";
import style from "./style.module.css";

const Cell = ({ onClick, x, y }) => {
    const [show, setShow] = useState(false)

    const toggleDot = () => {
        setShow(!show);
        onClick({x, y});
    }
    return <div className={style.cell} onClick={toggleDot}>
                { show && <img className={style.dot} src={require('./../../img/dot.png')} />}
           </div>
}

export default Cell;
