import React, {useEffect, useState} from "react"
import Cell from "./cell"
import style  from "./style.module.css"

const Grid = ({ map } ) => {

    const [cells, setCells] = useState([]);
    const [playerActive, setPlayerActive] = useState({});
    useEffect (() => {
        const cells = [];
        for(let x = 0; x < 10; x++) {
            for(let y = 0; y < 10; y++) {
                cells.push({x, y, player: false, activated: false })
            }
        }
        cells[55].player = true;
        cells[33].player = true;
        setCells(cells);
    }, []);

    const toggleCells = ({x, y}) => {
        const temp = [...cells];
        if(playerActive.x === x && playerActive.y === y) {
            temp[(10*x)-1+y].activated = false;
            temp[(10*x)+1+y].activated = false;
            temp[(10*(x-1))+y].activated = false;
            temp[(10*(x+1))+y].activated = false;
            setPlayerActive({});
        } else {
            if(playerActive.x) {
                const {x, y} = playerActive;
                temp[(10*x)-1+y].activated = false;
                temp[(10*x)+1+y].activated = false;
                temp[(10*(x-1))+y].activated = false;
                temp[(10*(x+1))+y].activated = false;
            }
            if(!temp[(10*x)-1+y].player) temp[(10*x)-1+y].activated = true;
            if(!temp[(10*x)+1+y].player) temp[(10*x)+1+y].activated = true;
            if(!temp[(10*(x-1))+y].player)temp[(10*(x-1))+y].activated = true;
            if(!temp[(10*(x+1))+y].player) temp[(10*(x+1))+y].activated = true;
            setPlayerActive({x, y});
        }
        setCells(temp);
    };

    const movePlayer = ({x, y}) => {
        const temp = [...cells];
        temp[10*x + y].player = true;
        temp[10*playerActive.x + playerActive.y].player = false;
        temp[(10*playerActive.x)-1+playerActive.y].activated = false;
        temp[(10*playerActive.x)+1+playerActive.y].activated = false;
        temp[(10*(playerActive.x-1))+playerActive.y].activated = false;
        temp[(10*(playerActive.x+1))+playerActive.y].activated = false;
        setCells(temp);
    }

    return <div className={style.gridSetting}>
       {cells.map((item) => <Cell 
            onClick={toggleCells}
            onMove={movePlayer}
            x={item.x} 
            y={item.y} 
            player={item.player}
            activated={item.activated}
        />)}   
    </div>;
}

export default Grid;
