import React from "react";
import style from "./style.module.css";
import Character from "./character";

const Cell = ({ onClick, onMove, onAttack, x, y, player, activated, usable, turn }) => {

    const toggleDot = () => {
        if(!usable) return;
        if(player.tired) return;
        if(activated && player) {
            onAttack({x,y});
            return
        }
        if(player) {
            if(player.team !== turn) return;
            onClick({x, y});
            return
        }
        if(activated) {
            onMove({x, y});
            return
        }
        return
    }

    return <div className={`${style.cell} ${player.team === 'blue' && player.team === turn && style.blue} ${player.team === 'red' &&  player.team === turn && style.red} ${usable && activated && style.activated} ${!usable && style.noUsable}`} onClick={toggleDot}>
                { player && player.currentHP > 0 && <Character player={player} turn={turn} />}
           </div>
}

export default Cell;
