import React, {useEffect, useState} from "react"
import Cell from "./cell"
import style  from "./style.module.css"

const Grid = ( ) => {

    const [cells, setCells] = useState([]);
    useEffect (() => {
        const cells = [];
        for(let x = 0; x < 10; x++) {
            for(let y = 0; y < 10; y++) {
                cells.push({x, y})
            }
        }
        setCells(cells);
    }, []);

    const toggleCells = ({x, y}) => {
        cells.map((item) => {
            if(item.x === x && item.y === y) item.player = true;
            else item.player = false;
        })
    };

    return <div className={style.gridSetting}>
       {cells.map((item) => <Cell onClick={toggleCells} x={item.x} y={item.y} showPlayer={item.player}/>)}   
    </div>;
}

export default Grid;
