import React, {useEffect, useState} from "react"
import Cell from "./cell"
import style  from "./style.module.css"

const Grid = ( ) => {

    const [cells, setCells] = useState([[]]);
    useEffect (() => {
        const cells = [];
        for(let x = 0; x < 10; x++) {
            cells[x] = [];
            for(let y = 0; y < 10; y++) {
                cells[x][y] = ({x, y, player: false});
            }
        }
        setCells(cells);
    }, []);

    const toggleCells = ({x, y}) => {
        const temp = cells;
        temp[x][y].player = true;
        setCells(temp);
        console.log(temp); 
    };

    return <div className={style.gridSetting}>
       {cells.map((row, x) => (row.map((item, y) => <Cell onClick={toggleCells} x={x} y={y} player={item.player}/>)))}   
    </div>;
}

export default Grid;
