import React, { useState } from "react";
import style from "./style.module.css";

const Cell = ({ onClick, x, y, player }) => {

    const toggleDot = () => {
        if(player) onClick({x, y});
    }
    return <div className={style.cell} onClick={toggleDot}>
                { player && <img className={style.dot} src={require('./../../img/dot.png')} />}
           </div>
}

export default Cell;
