import React from "react";
import style from "./style.module.css";

const Cell = ({ onClick, onMove, onAttack, x, y, player, activated, usable, turn }) => {

    const toggleDot = () => {
        if(!usable) return;
        if(activated && player) {
            onAttack({x,y});
            return
        }
        if(player) {

            if(player.team !== turn) return;
            onClick({x, y});
            return
        }
        if(activated) {
            onMove({x, y});
            return
        }
        return
    }

    return <div className={`${style.cell} ${player.team === 'blue' && style.blue} ${player.team === 'red' && style.red} ${usable && activated && style.activated} ${!usable && style.noUsable}`} onClick={toggleDot}>
                { player && player.currentHP > 0 && 
                <React.Fragment>
                    <img className={style.dot} src={require('./../../img/dot.png')} />
                </React.Fragment>}
           </div>
}

export default Cell;
